import React from "react";
import { Form, Input, Button, Avatar, Card, Icon } from "antd";

const dummy = {
  isLoggedIn: true,
  imagePaths: [],
  mainPosts: [
    {
      User: {
        id: 1,
        nickname: "arcadeKid"
      },
      content: 'A very first post',
      img: 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/49/Asa_Akira_2_2014.jpg/440px-Asa_Akira_2_2014.jpg'
    }
  ]
};

const Home = () => {
  return (
    <>
      {dummy.isLoggedIn && (
        <Form encType="multipart/form-data">
          <Input.TextArea
            maxLength={140}
            placeholder="Tell your story to share with Dear"
          />
          <div>
            <input type="file" multiple hidden />
            <Button>Upload Image</Button>
            <Button type="primary" htmlType="submit" style={{ float: "right" }}>
              Post
            </Button>
          </div>
          <div>
            {dummy.imagePaths.map((v, i) => {
              return (
                <div key={v} style={{ display: "inline-block" }}>
                  <img
                    src={"http://localhost:3000/" + v}
                    style={{ width: "200px" }}
                    alt={v}
                  />
                  <div>
                    <Button>Remove</Button>
                  </div>
                </div>
              );
            })}
          </div>
        </Form>
      )}
      {dummy.mainPosts.map(c => {
        return (
          <Card
            key={+c.createdAt}
            cover={c.img && <img src={c.img} />}
            actions={[
              <Icon type="retweet" key="retweet" />,
              <Icon type="heart" key="heart" />,
              <Icon type="message" key="message" />,
              <Icon type="ellipsis" key="ellipsis" />
            ]}
            extra={<Button>Follow</Button>}
          >
            <Card.Meta
              avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
              title={c.User.nickname}
              description={c.content}
            />
          </Card>
        );
      })}
    </>
  );
};

export default Home;
