import React from "react";
import Link from "next/link";
import PropTypes from 'prop-types';
import { Menu, Input, Button, Row, Col, Card, Avatar, Form } from "antd";
import LoginForm from './LoginForm'

const dummy = {
  nickname: "pikachu",
  Post: [],
  Following: [],
  Followers: [],
  isLoggedIn: false,
};

const Layout = ({ children }) => {
  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item key="home">
          <Link href="/">
            <a>Dear</a>
          </Link>
        </Menu.Item>
        <Menu.Item key="profile">
          <Link href="/profile">
            <a>Profile</a>
          </Link>
        </Menu.Item>

        <Menu.Item key="mail">
          <Input.Search enterButton style={{ verticalAlign: "middle" }} />
        </Menu.Item>
      </Menu>
      <Link href="/signup">
        <Button>Sign up</Button>
      </Link>
      <Row gutter={8}>
        <Col xs={24} md={6}>
          {dummy.isLoggedIn ? (
            <Card
              actions={[
                <div key="tweet">
                  <p>Tweet</p>
                  {dummy.Post.length}
                </div>,
                <div key="following">
                  <p>Following</p>
                  {dummy.Post.length}
                </div>,
                <div key="follower">
                  <p>Followers</p>
                  {dummy.Post.length}
                </div>
              ]}
            >
              <Card.Meta
                avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                title={dummy.nickname}
              />
            </Card>
          ) : (
            <LoginForm />
          )}
        </Col>
        <Col xs={24} md={12}>

        </Col>
        {children}
        <Col xs={24} md={6}>

        </Col>
      </Row>
    </div>
  );
};

Layout.propTypes = {
  children: PropTypes.node,
};

export default Layout;
