export const initState = {
  isLoggedIn: false,
  user: { nickname: "Asa Akira", Post: [], Following: [], Followers: [] }
};

export const LOG_IN = "LOG_IN"; // 액션의 이름
export const LOG_OUT = "LOG_OUT";

export const loginAction = {
  type: LOG_IN,
  data: {
    nickname: "Asa Akira"
  }
};

export const logoutAction = {
  type: LOG_OUT
};

const reducer = (state = initState, action) => {
  switch (action.type) {
    case LOG_IN:
      // console.log(action.data)
      return {
        ...state,
        isLoggedIn: true,
        user: action.data
      };
    case LOG_OUT:
      return {
        ...state,
        isLoggedIn: false,
        user: null
      };

    default:
      return state;
  }
};

export default reducer;
