import React, { useState, useCallback } from "react";
/*
    함수 컴포넌트는 state가 바뀌면 signup.js 컴포넌트가 통째로 재실행 됨
    따라서 함수가 새로 생성됨
    그 함수를 전달받은 자식 컴포넌트들은 렌더링을 다시함 <Form onSubmit={onSubmit}>

*/
import Head from "next/head";
import { Form, Input, Checkbox, Button } from "antd";

import Layout from "../components/Layout";

const Signup = () => {
  //   const [id, setId] = useState("");
  //   const [nick, setNick] = useState("");
  //   const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [term, setTerm] = useState(false);
  const [termError, setTermError] = useState(false);

  //  custom hook
  const useInput = (initValue = null) => {
    const [value, setter] = useState(initValue);
    const handler = useCallback(e => {
      setter(e.target.value);
    }, []);
    return [value, handler];
  };

  const [id, onChangeId] = useInput("");
  const [nick, onChangeNick] = useInput("");
  const [password, onChangePassword] = useInput("");

  const onSubmit = useCallback(e => {
    e.preventDefault();
    if (password !== passwordConfirm) {
      return setPasswordError(true);
    }
    if (!term) {
      return setTermError(true);
    }

    console.log({
      id,
      nick,
      password,
      passwordConfirm,
      term
    });
  }, []);

  /*
  const onChangeId = e => {
    setId(e.target.value);
  };

  const onChangeNick = e => {
    setNick(e.target.value);
  };

  const onChangePassword = e => {
    setPassword(e.target.value);
  };
  */

  const onChangePasswordConfirm = e => {
    setPasswordError(e.target.value !== password);
    setPasswordConfirm(e.target.value);
  };

  const onChangeTerm = e => {
    setTermError(false);
    setTerm(e.target.checked);
  };

  return (
    <>
      <Head>
        <title>Dear</title>
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.18.1/antd.css"
        />
        <script src="https://cdnjs.cloudflare.com/ajax/libs/antd/3.18.1/antd.js" />
      </Head>
      <Layout>
        <h1>Sign up</h1>
        <Form onSubmit={onSubmit} style={{ padding: 10 }}>
          <div>
            <label htmlFor="user-id">ID</label>
            <Input
              type="text"
              name="user-id"
              value={id}
              required
              onChange={onChangeId}
            />
          </div>
          <div>
            <label htmlFor="user-nick">Nick Name</label>
            <Input
              name="user-nick"
              value={nick}
              required
              onChange={onChangeNick}
            />
          </div>
          <div>
            <label htmlFor="user-password">Password</label>
            <Input
              name="user-password"
              type="password"
              required
              onChange={onChangePassword}
              value={password}
            />
          </div>
          <div>
            <label htmlFor="user-password-confirm">Password Confirm</label>
            <Input
              name="user-password-confirm"
              type="password"
              required
              onChange={onChangePasswordConfirm}
              value={passwordConfirm}
            />
            {passwordError && (
              <div style={{ color: "red" }}>Password should match</div>
            )}
          </div>
          <div>
            <Checkbox name="user-term" onChange={onChangeTerm} value={term}>
              I agree with the term
            </Checkbox>
            {termError && (
              <div style={{ color: "red" }}>
                Please check the box 'I agree with the term'
              </div>
            )}
          </div>
          <div>
            <Button type="primary" htmlType="submit">
              Done
            </Button>
          </div>
        </Form>
      </Layout>
    </>
  );
};

export default Signup;
