import React from "react";
import Link from "next/link";
import PropTypes from "prop-types";

import { Menu, Input, Button, Row, Col, Card, Avatar } from "antd";

const dummy = {
  nickname: "pikachu",
  Post: [],
  Following: [],
  Followers: []
};

const Layout = ({ children }) => {
  return (
    <>
      <Menu mode="horizontal">
        <Menu.Item key="home">
          <Link href="/">
            <a>Dear</a>
          </Link>
        </Menu.Item>
        <Menu.Item key="profile">
          <Link href="/profile">
            <a>Profile</a>
          </Link>
        </Menu.Item>

        <Menu.Item key="mail">
          <Input.Search enterButton style={{ verticalAlign: "middle" }} />
        </Menu.Item>
      </Menu>
      <Link href="/signup">
        <Button>Sign up</Button>
      </Link>
      <Row>
        <Col xs={24} md={6}>
          <Card
            actions={[
              <div key="dear"><p>Dear</p>{dummy.Post.length}</div>,
              <div key="following"><p>Following</p>{dummy.Post.length}</div>,
              <div key="follower"><p>Followers</p>{dummy.Post.length}</div>
            ]}
          >
            <Card.Meta
              avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
              title={dummy.nickname}
            />
          </Card>
          <Form>
            <div>
              <label htmlFor="user-id">ID</label>
              <br/>
              <Input name="user-id" value={userId} onChange={onChangeId} required></Input>
            </div>
          </Form>
          <Link href="/signup">
            <Button>
              <a>Sign up</a>
            </Button>
          </Link>
        </Col>
        <Col xs={24} md={12}>
          2
        </Col>
        {children}
        <Col xs={24} md={6}>
          3
        </Col>
      </Row>
    </>
  );
};

Layout.propTypes = {
  chilrdren: PropTypes.node
};

export default Layout;
