import React from "react";
import PropTypes from "prop-types";
import { Icon } from "antd";

const PostImages = ({ images }) => {
  if (images.length === 1) {
    return <img src={`http://localhost:3306/${images[0].src}`} />;
  } else if (images.length === 2) {
    return (
      <div>
        <img src={`http://localhost:3306/${images[0].src}`} width="50%" />
        <img src={`http://localhost:3306/${images[1].src}`} width="50%" />
      </div>
    );
  } else {
    return (
      <div>
        <img src={`http://localhost:3306/${images[0].src}`} width="50%" />
        <div
          style={{
            display: "inline-block",
            width: "50%",
            textAlign: "center",
            verticalAlign: "middle"
          }}
        >
          <Icon type="plus" />
          <br />
          {images.length - 1}
          more image load
        </div>
      </div>
    );
  }
};

PostImages.propTypes = {
  images: PropTypes.arrayOf(
    PropTypes.shape({
      src: PropTypes.string
    })
  ).isRequired
};
export default PostImages;
