import React, { useState } from "react";
import { Menu, Input, Link, Button, Row, Col, Card, Avatar, Form } from "antd";
import { useInput } from "../signup";

const LoginForm = () => {
  return (
    <div>
      <Form>
        <div>
          <label htmlFor="user-id">ID</label>
          <br />
          <Input name="user-id" required />
        </div>
        <div>
          <label htmlFor="user-id">Password</label>
          <br />
          <Input name="user-password" required />
        </div>
        <div>
          <Button type="primary" loading={false}>
            Log In
          </Button>
          <Link href="/signup">
            <Button>
              <a>Sign up</a>
            </Button>
          </Link>
        </div>
      </Form>
    </div>
  );
};

export default LoginForm;
